    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Log in!{% endblock %}

{% block body %}
  <form method=\"post\" action=\"{{ path('app_login_check') }}\">
    {% if error %}
      <div class=\"alert alert-danger\">
        {{ error.messageKey|trans(error.messageData, 'security') }}
      </div>
    {% endif %}

    {% if app.user %}
      <div class=\"mb-3\">
        You are logged in as {{ app.user.userIdentifier }},
        <a href=\"{{ path('app_logout') }}\">Logout</a>
      </div>
    {% endif %}

    <h1 class=\"h3 mb-3 font-weight-normal\">Please log in</h1>
    
    {# Champ d'entrée de l'utilisateur #}
    <label for=\"inputUsername\">Username</label>
    <input
      type=\"text\"
      value=\"{{ last_username }}\"
      name=\"_username\"
      id=\"inputUsername\"
      class=\"form-control\"
      autocomplete=\"username\"
      required
      autofocus
    />

    {# Champ d'entrée du mot de passe #}
    <label for=\"inputPassword\">Password</label>
    <input
      type=\"password\"
      name=\"password\"
      id=\"inputPassword\"
      class=\"form-control\"
      autocomplete=\"current-password\"
      required
    />

    {# Champ CSRF #}
    <input
      type=\"hidden\"
      name=\"_csrf_token\"
      value=\"{{ csrf_token('authenticate') }}\"
    />

    <button class=\"btn btn-lg btn-primary\" type=\"submit\">Sign in</button>
    <a href=\"{{ path('app_home') }}\">Home</a>
  </form>
{% endblock %}
", "security/login.html.twig", "/Users/dramsss/Documents/Projets Perso/Symfony/forum-test/templates/security/login.html.twig");
    }
